mapper.select:
SELECT count(*) FROM xl_exam h where create_id = #{createId}
and class_Id = #{classId}
and type = #{type}
and start_time > NOW()
and end_time > NOW() and NOW() > start_time
and NOW() > end_time

SELECT id, NAME, notice, start_time starttime, end_time endtime, class_id classId, type, point_score pointscore, create_id createid, create_date createdate, update_id updateid, update_date updatedate, enable_flag enableflag, ( SELECT count(id) FROM xl_exam_student es WHERE es.exam_id = e.id ) totalStudent, ( SELECT count(id) FROM xl_exam_student es WHERE complate IN ('T', 'S') AND es.exam_id = e.id ) complateStudent, CASE WHEN NOW() > end_time THEN 2 WHEN NOW() > start_time AND end_time > NOW() THEN 1 WHEN start_time > NOW() THEN 0 END status FROM xl_exam e WHERE create_id = #{createId}
and class_Id = #{classId}
and type = #{type}
and start_time > NOW()
and end_time > NOW() and NOW() > start_time
and NOW() > end_time
ORDER BY create_date desc limit #{start},#{end}
