mapper.select:
SELECT count(*) FROM xl_homework h where create_id = #{createId}
and clas_id = #{clasId,jdbcType=BIGINT}
and end_time > NOW()
and NOW() > end_time

SELECT h.id , h. NAME NAME , h.end_time endtime , h.clas_id clasid , h.remark remark , h.create_id createid , h.create_date createdate , ( SELECT GROUP_CONCAT(b. NAME , '||') FROM ( SELECT CONCAT(u. NAME , ',' , s. NAME) NAME , '1' AS id , hd.homework_id workid FROM xl_section_detail sd LEFT JOIN xl_section s ON s.id = sd.section_id LEFT JOIN xl_unit u ON u.id = s.unit_id LEFT JOIN xl_homework_detail hd ON sd.id = hd.section_detail_id GROUP BY u.id , s.id , hd.homework_id ) b WHERE b.workid = h.id GROUP BY b.id ) detail , ( SELECT count(*) FROM xl_homework_student hs WHERE hs.homework_id = h.id ) totalStudent , ( SELECT count(*) FROM xl_homework_student hs WHERE hs.homework_id = h.id AND hs.work_complate = 'T' ) complateTotal , IF(end_time > NOW() , '1' , '2') STATUS FROM xl_homework h WHERE h.create_id = #{createId}
and clas_id = #{clasId,jdbcType=BIGINT}
and h.end_time > NOW()
and NOW() > h.end_time
order by h.id desc limit #{start},#{end}

SELECT count(*) FROM xl_homework xh LEFT JOIN xl_homework_student hs ON xh.id = hs.homework_id WHERE hs.student_id = #{studentId}
and xh.end_time > NOW()
and NOW() > xh.end_time

SELECT xh.id id, xh. name, xh.remark, xh.end_time endTime, xh.create_date createDate, hs.score, hs.work_complate workComplate, hs.work_time workTime, hs.student_id studentId, ( SELECT count(id) FROM xl_homework_student xhs WHERE xhs.homework_id = xh.id ) totalStudent, ( SELECT count(id) FROM xl_homework_student xhs WHERE xhs.homework_id = xh.id AND xhs.work_complate = 'T' ) complateStudent, IF (xh.end_time > NOW(), 'T', 'F') over FROM xl_homework xh LEFT JOIN xl_homework_student hs ON xh.id = hs.homework_id WHERE hs.student_id = #{studentId}
and xh.end_time > NOW()
and NOW() > xh.end_time
order by xh.create_date desc limit #{start},#{end}
