mapper.select:
SELECT count(*) FROM xl_read_article where 1=1
and level = #{level,jdbcType=VARCHAR}
and type = #{type,jdbcType=VARCHAR}

SELECT xra.id, xra. NAME name, xra.word_num wordNum, xra.picture_path picturePath, xra.type, xra. LEVEL level, xra.create_id createId, xra.create_date createDate, xra.update_id updateId, xra.update_date updateDate, xra.enable_flag enableFlag, dd. NAME levelName, dd1. NAME typeName FROM xl_read_article xra LEFT JOIN xl_dic_detail dd ON dd.dic_id = 15 AND dd.name_val = xra.`level` LEFT JOIN xl_dic_detail dd1 ON dd1.dic_id = 16 AND dd1.name_val = xra.type WHERE 1=1
and xra.level = #{level,jdbcType=VARCHAR}
and xra.type = #{type,jdbcType=VARCHAR}
order by xra.id desc limit #{start},#{end}
