mapper.select:
select 

from xl_user
where enable_flag='T' 
  
    and mobile = #{mobile,jdbcType=VARCHAR}
  
  
    and name_num = #{nameNum,jdbcType=VARCHAR}

SELECT count(*) FROM xl_user u LEFT JOIN xl_user_clas uc ON u.id = uc.user_id WHERE u.user_batch = 1 AND uc.clas_id = #{clasId}
and u.update_date is null
and u.update_date is not null

SELECT u. NAME name, u.mobile mobile, u.name_num nameNum, IF (u.update_date IS NULL, 0, 1) used FROM xl_user u LEFT JOIN xl_user_clas uc ON u.id = uc.user_id WHERE u.user_batch = 1 AND uc.clas_id = #{clasId}
and u.update_date is null
and u.update_date is not null
order by u.update_date desc limit #{start},#{end}
